### Multi-step CBC encryption with plaintext CryptoKey in blocking return mode # #include<ti/drivers/AESCBC.h>
#include<ti/drivers/cryptoutils/cryptokey/CryptoKeyPlaintext.h>

...

#defineAES_BLOCK_SIZE16//bytes

AESCBC_Handlehandle;
CryptoKeycryptoKey;
int_fast16_tencryptionResult;

//Forexamplepurposesonly.GenerateIVsinanon-staticwayinpractice.
//Testvector0fromNISTCAPVsetCBCMMT128
uint8_tiv[16]={0x2f,0xe2,0xb3,0x33,0xce,0xda,0x8f,0x98,
0xf4,0xa9,0x9b,0x40,0xd2,0xcd,0x34,0xa8};
uint8_tplaintext[16]={0x45,0xcf,0x12,0x96,0x4f,0xc8,0x24,0xab,
0x76,0x61,0x6a,0xe2,0xf4,0xbf,0x08,0x22};
uint8_tciphertext[sizeof(plaintext)];
uint8_tkeyingMaterial[16]={0x1f,0x8e,0x49,0x73,0x95,0x3f,0x3f,0xb0,
0xbd,0x6b,0x16,0x66,0x2e,0x9a,0x3c,0x17};

//Theciphertextshouldbethefollowingaftertheencryptionoperation:
//0x0f,0x61,0xc4,0xd4,0x4c,0x51,0x47,0xc0
//0x3c,0x19,0x5a,0xd7,0xe2,0xcc,0x12,0xb2


handle=AESCBC_open(0,NULL);

if(handle==NULL){
//handleerror
}

CryptoKeyPlaintext_initKey(&cryptoKey,keyingMaterial,sizeof(keyingMaterial));

AESCBC_SegmentedOperationoperation;
AESCBC_SegmentedOperation_init(&operation);

operation.input=plaintext;
operation.output=ciphertext;
operation.inputLength=sizeof(plaintext);

encryptionResult=AESCBC_setupEncrypt(handle,&cryptoKey);
if(encryptionResult!=AESCBC_STATUS_SUCCESS){
//handleerror
}

encryptionResult=AESCBC_setIV(handle,iv,AES_BLOCK_SIZE);
if(encryptionResult!=AESCBC_STATUS_SUCCESS){
//handleerror
}

encryptionResult=AESCBC_addData(handle,&operation);
if(encryptionResult!=AESCBC_STATUS_SUCCESS){
//handleerror
}

operation.inputLength=0;
encryptionResult=AESCBC_finalize(handle,&operation);
if(encryptionResult!=AESCBC_STATUS_SUCCESS){
//handleerror
}

AESCBC_close(handle);
